(494, 153, '_form', '<div class=\"customer-info\">\n[text* nom placeholder \"Nom\"] </label>\n\n[text* prenom placeholder \"Prénom\"] </label>\n\n[email* email placeholder \"E-mail\"] </label>\n\n[text* adresse placeholder \"Adresse complète\"] </label>\n\n[tel telephone placeholder \"Téléphone (facultatif)\"] </label>\n</div>\n<br/>\n<h4 class=\"font-niconne pink-font\"><B> Malices </B></h4>\n\n<div class=\"container\">[checkbox checkSiSu20 \"Sirop de fleur de sureau 20cl -  5.50€\"] <FONT color=\"white\">XXXXXXXXXX</FONT> [number qte1 placeholder \"Quantité\"]</div>\n<div class=\"container\">[checkbox checkSiSu50 \"Sirop de fleur de sureau 50cl - 9.50€\"] <FONT color=\"white\">XXXXXXXXXX</FONT> [number qte2 placeholder \"Quantité\"]</div>\n\n<br/>\n\n<div class=\"container\">[checkbox checkApSu20 \"Apéritif à la fleur de sureau 20cl - 7.50€\"] <FONT color=\"white\">XXXXXXXX</FONT>[number qte3 placeholder \"Quantité\"]</div>\n<div class=\"container\">[checkbox checkApSu70 \"Apéritif à la fleur de sureau 70cl - 17€\"] <FONT color=\"white\">XXXXXXXXX</FONT> [number qte4 placeholder \"Quantité\"]</div>\n<div class=\"container\">[checkbox checkApPru20 \"Apéritif de prunellier sauvage 20cl - 7.50€\"] <FONT color=\"white\">XXXXXX</FONT> [number qte5 placeholder \"Quantité\"]</div>\n<div class=\"container\">[checkbox checkApPru70 \"Apéritif de prunellier sauvage 70cl - 17€\"] <FONT color=\"white\">XXXXXXX</FONT> [number qte6 placeholder \"Quantité\"]</div>\n<div class=\"container\">[checkbox checkApNoi20 \"Apéritif à la noisette 20cl - 7.50€\"] <FONT color=\"white\">XXXXXXXXXXXXX</FONT>[number qte7 placeholder \"Quantité\"]</div>\n<div class=\"container\">[checkbox checkApNoi70 \"Apéritif à la noisette 70cl - 17€\"] <FONT color=\"white\">XXXXXXXXXXXXXX</FONT> [number qte8 placeholder \"Quantité\"]</div>\n\n<br/>\n\n<div class=\"container\">[checkbox checkCo20 \"Coffret 20cl Sirop fleur de sureau + Apéritif fleur de sureau + Apéritif prunellier sauvage - 19€\"]  [number qte9 placeholder \"Quantité\"]</div>\n\n<br/>\n<label> Frais de livraison variable selon commande </label>\n\n\n<label> Votre message\n[textarea message] </label>\n\n[recaptcha]\n\n[submit \"Envoyer\"]'),
